        Slot storage r;

        assembly {
            r.slot := slot
        }

        r.value[idx] = val;
    }

    function push(uint256 variant) internal {
        bytes32 slot = REPORTS_VARIANTS_SLOT;

        Slot storage r;

        assembly {
            r.slot := slot
        }

        r.value.push(variant);
